    public static ArrayList<Shoes> getListData() {
        ArrayList<Shoes> list = new ArrayList<>();
        for (String[] sData : data) {
            Shoes shoes = new Shoes();
            shoes.setNameShoes(sData[0]);
            shoes.setInfoShoes(sData[1]);
            shoes.setShoesBadge(sData[2]);
            shoes.setShopShoes(sData[3]);
            shoes.setPhotoShoes(sData[4]);

            list.add(shoes);
        }
        return list;
    }
